
import IWhoQuery = require('../whoIAm/IWhoQuery');

import IWho = require('../whoIAm/IWho');

import IEventer = require('../eventing/IEventer');

interface ICollection<whoType extends IWho> extends IEventer {
    add(item: whoType);
    addMany(items: whoType[]);
    remove(query: IWhoQuery, callback?: (e: Error, result: whoType[]) => void);
    get(query: IWhoQuery, callback: (e: Error, result: whoType[]) => void);
    clear(): void;
    list(): whoType[];
    each(iterator: (item: whoType, itemDone: (e: Error) => void) => void, allDone?: (e: Error) => void);
}

export = ICollection;
